import { Component, inject, signal } from '@angular/core';
import { IaService } from './services/ia.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  iaService = inject(IaService);
  title = 'front-menu-restaurants-app';
  markdown = signal<string>("");
  loading = signal<boolean>(false);
  question: string = "";
  /*markdown = `## Markdown __rulez__!
  ---

  ### Syntax highlight
  \`\`\`typescript
  const language = 'typescriptaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa';
  \`\`\`

  ### Lists
  1. Ordered list
  2. Another bullet point
     - Unordered list
     - Another unordered bullet

  ### Blockquote
  > Blockquote to the max`;*/

  constructor() { }


  queryLLM = () => {
    console.log(this.question)
    this.iaService.answerQuestion(this.question).subscribe((response) => {
      console.log(response);
      this.markdown.set(response);
    })
  }

}
